insert&quot;:&quot;الفسفور                          816 غراماً&quot;},{&quot;attributes&quot;:{&quot;align&quot;:&quot;right&quot;},&quot;insert&quot;:&quot;\\\\n&quot;},{&quot;insert&quot;:&quot; البوتاسيوم               669 ملغراماً&quot;},{&quot;attributes&quot;:{&quot;align&quot;:&quot;right&quot;},&quot;insert&quot;:&quot;\\\\n&quot;},{&quot;insert&quot;:&quot; المغنيسيوم             276 ملغراماً&quot;},{&quot;attributes&quot;:{&quot;align&quot;:&quot;right&quot;},&quot;insert&quot;:&quot;\\\\n&quot;},{&quot;insert&quot;:&quot;الحديد              7.36 ميكروغرامات&quot;},{&quot;attributes&quot;:{&quot;align&quot;:&quot;right&quot;},&quot;insert&quot;:&quot;\\\\n&quot;},{&quot;insert&quot;:&quot;الكالسيوم                    84 ملغراماً&quot;},{&quot;attributes&quot;:{&quot;align&quot;:&quot;right&quot;},&quot;insert&quot;:&quot;\\\\n&quot;},{&quot;insert&quot;:&quot;الصوديوم                    3 ملغرامات&quot;},{&quot;attributes&quot;:{&quot;align&quot;:&quot;right&quot;},&quot;insert&quot;:&quot;\\\\n&quot;},{&quot;insert&quot;:&quot;حمض الفوليك         87 ميكروغراماً&quot;},{&quot;attributes&quot;:{&quot;align&quot;:&quot;right&quot;},&quot;insert&quot;:&quot;\\\\n&quot;},{&quot;insert&quot;:&quot;فيتامين ب3            1.499 ملغرام&quot;},{&quot;attributes&quot;:{&quot;align&quot;:&quot;right&quot;},&quot;insert&quot;:&quot;\\\\n&quot;},{&quot;insert&quot;:&quot;فيتامين ب6            0.186 ملغرام&quot;},{&quot;attributes&quot;:{&quot;align&quot;:&quot;right&quot;},&quot;insert&quot;:&quot;\\\\n\\\\n\\\\n&quot;},{&quot;attributes&quot;:{&quot;bold&quot;:true},&quot;insert&quot;:&quot;التأثيرات الجانبية للشوفان&quot;},{&quot;attributes&quot;:{&quot;align&quot;:&quot;right&quot;,&quot;header&quot;:1},&quot;insert&quot;:&quot;\\\\n&quot;},{&quot;insert&quot;:&quot;تعتبر نخالة الشوفان آمنة للاستخدام بالنسبة لمعظم الأشخاص، وفيما يأتي بعض التحذيرات حول استخدام الشوفان بالنسبة لفئات معينة من الأشخاص:&quot;},{&quot;attributes&quot;:{&quot;align&quot;:&quot;right&quot;},&quot;insert&quot;:&quot;\\\\n\\\\n&quot;},{&quot;insert&quot;:&quot;يمكن أن تسبب نخالة الشوفان بعض التاثيرات الجانبية عند استهلاكها، مثل الغازات، والانتفاخ، وللتخفيف من هذه الآثار، يمكن البدء بتناول كميات صغيرة، وزيادتها ببطء، وسوف يعتاد الجسم على نخالة الشوفان بمرور الوقت، وستختفي غالباً الآثار الجانبية التي تسببها.&quot;},{&quot;attributes&quot;:{&quot;align&quot;:&quot;right&quot;},&quot;insert&quot;:&quot;\\\\n&quot;},{&quot;insert&quot;:&quot;يمكن أن يتلوِّث الشوفان بالغلوتين، فعلى الرغم من عدم احتواء الشوفان على الغلوتين، ولكنَّه يمكن أن يزرع في حقول القمح أو الشعير، كما يمكن أن تتم معالجته بالآلات المستخدمة في معالجة الحبوب الأخرى المحتوية على الغلوتين، ولذا يجب على الأشخاص الذين يعانون من عدم تحمل الغلوتين (بالإنجليزية: Gluten intolerance)، أو الداء البطني (بالإنجليزية: Celiac disease) توخي الحذر عند تناول الشوفان، واختيار الأنواع المعتمدة والموثق بها أنَّها خالية من الغلوتين.&quot;},{&quot;attributes&quot;:{&quot;align&quot;:&quot;right&quot;},&quot;insert&quot;:&quot;\\\\n&quot;},{&quot;insert&quot;:&quot;\\\\n&quot;}]}', 1588912293),
