INSERT INTO `q_topic` (`id_topic`, `id_url`, `title`, `brief`, `long_brief`, `image`, `cover`, `follower`, `time_stamp`) VALUES
(1, 'اسلاميات', 'اسلاميات', 'يحتوي على العلوم الدينيه المختلفه والأحكام الشرعية للدين الإسلامي بما جاء به النبي محمد وبما أنزله الله عليه من تشريعات إلهية\r\n', '', 'space/image/icon islamic mohammad rasool allah.png', 'space/cover/islamic.png', 0, '2019-12-14 18:43:56'),
(2, 'التعليم', 'التعليم', 'يضم المحتوى العلوم المختلفة واللغات الموجودة على مستوى العالم والدراسات الموجودة على مستوى المدارس والجامعات بالاضافة الى القوانين العلمية والثوابت الرياضيه\r\n', '', 'space/image/education.png', 'space/cover/education.png', 0, '2019-12-17 18:42:34'),
(3, 'شخصيات-عظماء', 'شخصيات وعظماء', 'يضم المحتوى بعض الشخصيات ممن برز اسمهم عبر مر العصور والأزمان وممن سطر التاريخ اسمائهم بحروف من نور  كلا على حسب ما تميز به والمجال الذي لمع فيه   \r\n', '', '', 'space/cover/1.jpeg', 0, '2019-12-17 19:09:03'),
(4, 'الطهى', 'الطهى', 'يضم المحتوى الأغذية الموجودة علي مستوي العالم والاكلات الصحيه وكيفيه طهي الانواع المختلفه من الغذاء بطريقة صحيحة تحافظ على العناصر الغذائية الموجودة بالغذاء و تمد الجسم باحتياجاته الغذائية\r\n', '', '', 'space/cover/1.jpeg', 0, '2019-12-17 19:13:50'),
(5, 'التقنيات', 'التقنيات', 'يحتوي علي التطبيقات المهمة للتقنيات الحديثة التي ساعدت الإنسان في تغيير حياته للأفضل والبرامج الالكترونيه المختلفه والتصميمات الالكترونيه الحديثه بالاضافه الى البرمجة الحديثه المتطوره  \r\n', '', '', 'space/cover/1.jpeg', 0, '2019-12-17 19:16:34'),
(6, 'العلوم-الأرضية', 'العلوم الأرضية', 'يتضمن المحتوى على كافة أنواع العلوم الأرضية الموجودة على سطح الكرة الأرضية التي منها ما هو طبيعي ومنها ما هو بشري كما يحتوي على العلوم والظواهر الكونية وحركة الكواكب والنجوم في الفضاء الكوني   \r\n', '', '', 'space/cover/1.jpeg', 0, '2019-12-17 19:18:52'),
(7, 'مطبخ-فن ', 'مطبخ و فن', 'كل ما يخص عالم المأكولات و المشروبات ومستلزماته', 'كل ما يخص عالم المأكولات و المشروبات ومستلزماته', 'space/cover/1.jpeg', 'space/cover/1.jpeg', 0, '2019-12-17 22:57:07'),
(8, 'الرياضة', 'الرياضة', 'يتضمن المحتوى الأنواع المختلفة للألعاب الرياضية الموجودة على مستوى العالم والبرامج الرياضية للاعبين واصابات الملاعب وطرق العلاج كما يحتوي على النصائح الخاصة بكل مجال وتجنب الأخطار الناجمة من بعض تلك الألعاب \r\n', '', '', 'space/cover/1.jpeg', 0, '2019-12-18 19:06:21'),
(9, 'الفنون', 'الفنون', 'يتضمن محتوى الفنون بكافة انواعها والشخصيات الفنية المختلفة التي تقوم بصناعة الفن والاعمال المسرحية والدراما المتنوعة   ', 'يتضمن محتوى الفنون بكافة انواعها والشخصيات الفنية المختلفة التي تقوم بصناعة الفن والاعمال المسرحية والدراما المتنوعة   ', '', 'space/cover/1.jpeg', 0, '2019-12-18 19:25:16'),
(10, 'الزراعة', 'الزراعة', 'يتضمن المحتوى الزراعة بكافة انواعها المختلفه وطرق الزراعة وكيفية العناية بالنباتات المزروعة بطرق الري الحديثة والأسمدة والمبيدات للوصول بالمحصول إلى الوزن المثالي والجودة المطلوبة بالمواصفات العالمية', 'يتضمن المحتوى الزراعة بكافة انواعها المختلفه وطرق الزراعة وكيفية العناية بالنباتات المزروعة بطرق الري الحديثة والأسمدة والمبيدات للوصول بالمحصول إلى الوزن المثالي والجودة المطلوبة بالمواصفات', '', 'space/cover/1.jpeg', 0, '2019-12-18 19:31:00'),
(11, 'التغذية', 'التغذية', 'يضم المحتوى كافة أنواع الأنظمة الغذائية والعناصر الغذائية التي تمد الجسم بالطاقه والحيويه  ويضمن البناء الجسماني السليم بالإضافة إلى البرامج الغذائية المختلفة للرياضيين ولاعبي كمال الأجسام بما يضمن أقصى  استفادة من العناصر الموجودة بكافه أنواع الغذاء المختلفة \r\n', 'يضم المحتوى كافة أنواع الأنظمة الغذائية والعناصر الغذائية التي تمد الجسم بالطاقه والحيويه  ويضمن البناء الجسماني السليم بالإضافة إلى البرامج الغذائية المختلفة للرياضيين ولاعبي كمال الأجسام بما يضمن أقصى  استفادة من العناصر الموجودة بكافه أنواع الغذاء المختلفة \r\n', '', 'space/cover/1.jpeg', 0, '2019-12-18 22:26:18');
