(1152, 'من-أول-شهيد-في-الإسلام', 'من اول شهيد في الاسلام', 'ما معنى الشهاده في سبيل الله؟', 'منوعات اسلاميه', 1, 1152, 0, 0),
(1153, 'ما-هي-فوائد-الإقلاع-عن-التدخين', 'ما هي فواءد الاقلاع عن التدخين', 'يعدّ التوقف عن التدخين مفيدًا جداً للجسم في اي وقت كان، اذ يبدا الجسم باظهار ردود الفعل تدريجياً مع الوقت بمجرد التوقّف عن تلويثه بالسموم التي تحتويها السجاءر.', 'معلومات و نصاءح طبيه', 1, 1153, 0, 1),
(1154, 'كيفيه-الاقلاع-عن-التدخين-بالكتابة', 'كيفيه الاقلاع عن التدخين بالكتابه', 'كتابه خطه البحث بشكل عام تقوم على تكوين صوره اجماليه عامه عن البحث، ثمّ كتابه مفاصل البحث وعناوينه الرءيسيه التي سوف يتناولها في بحثه دون شرح او تفصيل.', 'فن الكتابه و التعبير', 1, 1154, 0, 1),
(1155, 'اضرار-التدخين-بعد-الأكل', 'اضرار التدخين بعد الاكل', 'اذا ما كان الجسم في حاله نشطه وخصوصاً نشاط في عمل الدوره الدمويه فان هذا يساهم بشكل كبير على امتصاص كميات اكبر من ماده النيكوتين، وقد اوضحت الدراسات التي اجريت على المدخنين الخطوره الشديده التي يسببها التدخين بعد الاكل مباشرهً على جسم المدخن.', 'معلومات و نصاءح طبيه', 1, 1155, 0, 1),
(1156, 'كيف-يمكن-التوقف-عن-التدخين', 'كيف يمكن التوقف عن التدخين', 'يعتبرُ التدخين واحداً من الافاتِ التي تضرّ بالمجتمع وافراده، فهذه الافه تسبّبُ العديد من الاضرارَ الجسديّه، والنفسيّه، وكذلك الاجتماعيّه، اضافهً الى الخساءر الماديه الكبيره للشخص والمجتمعات.', 'معلومات و نصاءح طبيه', 1, 1156, 0, 1),
(1157, 'ما-هو-الحب', 'ما هو الحب', 'يمكن تعريف الحب على انّه عطاء عميق وتاثر عاطفي بشخص اخر والشعور برباط دافء.', 'معلومات عامه', 1, 1157, 0, 1),
(1158, 'ما-هي-فوائد-عيش-الغراب', 'ما هي فواءد عيش الغراب', 'عيش الغراب او الفطر؛ عباره عن اجسام مثمره لانواع معينه من الفطريات التي تنمو في التربه السطحيه والحطام العضوي، ويتوفر الفطر باكثر من 16 الف نوع.', 'ما هي فواءد النباتات', 1, 1158, 0, 1),
(1159, 'اين-توجد-اكبر-زهرة-بالعالم', 'اين توجد اكبر زهره بالعالم', 'الازهار من النباتات المستخدمه بشكلٍ اساسي للزينه والتهادي بين الناس، واحياناً لتحضير المشروبات او تزيين الماكولات وغيرها.', 'نباتات', 1, 1159, 0, 1),
(1160, 'ما-هي-المناطق-السياحيه-في-كوريا', 'ما هي المناطق السياحيه في كوريا', 'تحتوي كوريا الجنوبيه على العديد من مناطق الجذب السياحيّ المتنوّعه.', 'مدن و بلدان', 1, 1160, 0, 1),
(1161, 'ما-هو-دعاء-دخول-السوق', 'ما هو دعاء دخول  السوق', 'دعاء يجب علينا ان نردده حينما ندخل الاسواق.', 'الادعيه', 1, 1161, 0, 1),
(1162, 'من-تكون-سيدة-نساء-العالمين', 'من تكون سيده نساء العالمين', 'ان السيده فاطمه سلام الله عليها والسيده مريم سلام الله عليها كلتاهما من النساء الاربع اللاتي تم ذكرهن من قبل النبي الكريم صلى الله عليه وسلم من حيث انهن اكمل النساء.', 'شخصيات اسلاميه', 1, 1162, 0, 1),
(1163, 'قاتل-ربع-سكان-العالم', 'قاتل ربع سكان العالم', 'كيف خلق الله ادم وحواء؟', 'منوعات اسلاميه', 1, 1163, 0, 0),
