(1299, 'ما-هو-حيوان-الوبر', 'ما هو حيوان الوبر', 'ما هو سلوك حيوان الوبر واين يسكن؟', 'حيوانات و طيور', 1, 1299, 0, 0),
(1300, 'ما-هو-اسم-أنثى-الطاووس', 'ما هو اسم انثى الطاووس', 'ما هي دوره حياه الطاووس؟', 'حيوانات و طيور', 1, 1300, 0, 0),
