(2529, 'كيفية-صنع-عصير-العنب', 'كيفيه صنع عصير العنب', 'العنب يعد من انواع الثمر الذي يكون ناعم القشره ، ويعرف العنب بانه نبات لبّي وعصيري المحتوى.', 'عصاءر', 1, 2529, 0, 1),
(2530, 'ما-هو-عصير-الحامض', 'ما هو عصير الحامض', 'يتمتّع عصير الحامض بمحتوى هاءل من الفيتامينات، والمعادن، والاحماض الدهنيّه، ذات الفواءد الصحّيّه المذهله.', 'عصاءر', 1, 2530, 0, 1),
(2531, 'ما-هي-أضرار-زراعة-الشعر', 'ما هي اضرار زراعه الشعر', 'نبحثُ داءماً عن ظهورنا بافضل شكلٍ امام الناس وامام انفسنا، لكن هذه الامنيه لا تتحقق داءماً، فهنالك الكثير من العراقيل التي قد تقف امامنا، واهمّها مشكله ضعف الشعر، وعدم نموّه بالشكل الطبيعي، او ما يعرف (بالصلع). فما هو الصلع؟ وما هي اسبابه ؟&nbsp;', 'معلومات عامه', 1, 2531, 0, 1),
(2532, 'ما-تفسير-ل-ن-س-ف-ع-ا-ب-الن-اص-ي-ة', 'ما تفسير لَنَسْفَعًا بِالنَّاصِيَهِ', 'جاء في معاجم اللغه العربيه ان معنى الناصيه في اللغه: مقدمه الراس.', 'تاملات قرانيه', 1, 2532, 0, 1),
(2533, 'من-هو-إبليس', 'من هو ابليس', 'اراد الله سبحانه وتعالى ان يخلق ادم عليه السّلام بيده ونفخ فيه من روحه لكي يستخلفه في الارض فيعمّرها ويعبد الله تعالى.', 'تاملات قرانيه', 1, 2533, 0, 1),
(2534, 'ما-تفسير-قوله-الوسواس-الخناس', 'ما تفسير قوله الوسواس الخناس', 'يقول الله جل و علا في سوره الناس ( قل اعوذ برب الناس. ملك الناس. اله الناس. من شر الوسواس الخناس، الذي يوسوس في صدور الناس.', 'تاملات قرانيه', 1, 2534, 0, 1),
(2535, 'ما-قصة-ذو-القرنين', 'ما قصه ذو القرنين', 'ذُكر ذو القرنين في سوره الكهف، وهو ملك صالح مسلم عابد لله تعالى، جاب الارض ينشر الاسلام ويدعو اليه.', 'تاملات قرانيه', 1, 2535, 0, 1),
(2536, 'ما-سبب-تسمية-ذو-القرنين-بهذا-الإسم', 'ما سبب تسميه ذو القرنين بهذا الاسم', 'اختلفَ المفسرون في بيان سبب تلقيب ذي القرنين بهذا الاسم، وقد ذُكرت العديدُ من الاسباب، الّا انّ جميعها لم يشتمل على دلالات واضحه ذات قيمه تءكّد ذلك.', 'معلومات اسلاميه', 1, 2536, 0, 1),
(2537, 'ما-فضل-الصلاة-في-الروضة', 'ما فضل الصلاه في الروضه', 'تعتبر الصلاه في الروضه مستحبّهً، ومن الاعمال التي يمكن ان يءدّيها المسلم هناك؛ الدعاء، والاعتكاف، وقراءه القران.', 'السنه النبويه', 1, 2537, 0, 1),
(2538, 'ما-فضل-زيارة-المدينة-المنورة', 'ما فضل زياره المدينه المنوره', 'تقع المدينه المنوره في شبه الجزيره العربيه، وبالتحديد في اقليم الحجاز الذي يحجز سهل تهامه عن مرتفعات نجد، وتبعد 160 كم عن البحر الاحمر شرقاً، و335 كم عن مكه المكرمه شمالاً.', 'معلومات اسلاميه', 1, 2538, 0, 1),
(2539, 'ما-هي-أسباب-محبة-الله-للعبد', 'ما هي اسباب محبه الله للعبد', 'المحبه بين المءمنين من نِعم الله العظمى، وهي تزيد الصله والمودّه بينهم، فعندما يُحبّ احدنا شخصاً وجب عليه اخباره بذلك.', 'منوعات', 1, 2539, 0, 1),
(2540, 'كيفية-صنع-أفلام-الكرتون', 'كيفيه صنع افلام الكرتون', 'منذ الصغر ونحنا نتابع افلام الكرتون التي كانت تشدنا لساعات طويله، هذه الافلام الجميله التي تحاكي براءه الطفوله.', 'افلام ', 1, 2540, 0, 1),
(2541, 'كم-عدد-نجوم-العلم-الأمريكي', 'كم عدد نجوم العلم الامريكي', 'تعتمد الولايات المتّحده الامريكيه علماً رسمياً لها يتالف من ثلاثه عشر خطاً احمر وابيض اللون، ويشير عدد هذه الخطوط الى عدد المُستعمرات الامريكيه التي كانت قاءمهً عند استقلالها عن بريطانيا العظمى', 'معلومات عامه', 1, 2541, 0, 1),
(2542, 'لما-سورة-التوبة-بدون-بسمله', 'لما سوره التوبه بدون بسمله', 'القران الكريم الذي انزله الله سبحانه وتعالى على رسوله ليكون معجزه الله الخالده لنبيّه كان مُعجزاً بكلّه واجزاءه.', 'تاملات قرانيه', 1, 2542, 0, 1),
(2543, 'ما-لقب-عامر-بن-عبد-الله-بن-الجراح', 'ما لقب عامر بن عبد الله بن الجراح', 'هو عامر بن عبد الله بن الجرّاح بن هلال بن وهيب بن ضبه بن الحارث بن فِهْر بن مالك بن النضر بن كنانه، يكنّى بابي عبيده، وامّه هي: اميمه بنت غنم بن جابر بن عبد العُزّى بن عامره بن عميره بن وديعه.', 'الصحابه و التابعون', 1, 2543, 0, 1),
(2544, 'من-الذي-ل-قب-بخطيب-الر-سول', 'من الذي لُقب بخطيب الرّسول', 'اُطلق لقب خطيب الرّسول -صلّى الله عليه وسلّم- على الصّحابي ثابت بن قيس رضي الله عنه.', 'الصحابه و التابعون', 1, 2544, 0, 1),
(2545, 'من-الذي-ل-قب-بذي-النورين', 'من الذي لُقب بذي النورين', 'عثمان بن عفَّان الاموي القريشي، ويكنى بابي عبد الله، وهو ثالث الخلفاء الراشدين.', 'الخلفاء الراشدين', 1, 2545, 0, 1),
(2546, 'لما-س-مي-بذو-النورين', 'لما سُمي بذو  النورين؟', 'ذو النورين وهو الصحابي الجليل وثالث الخلفاء الراشدين عثمان بن عفان الاموي القرشي ولد في عام 576 وتوفي في عام 656 ميلادي اي 47 قبل الهجره و35 هجري ، يعتبر سيدنا عثمان بن عفان احد المبشرين بالجنه.', 'الصحابه و التابعين', 1, 2546, 0, 1),
(2547, 'كيفية-جعل-الرموش-سوداء', 'كيفيه جعل الرموش سوداء', 'طرق لجعل الرموش سوداء.', 'تجميل و مكياج', 1, 2547, 0, 1),
(2548, 'كيف-اهتم-بصحتي', 'كيف اهتم بصحتي', 'طرق للاهتمام بالصحه الجسم والحفاظ عليه.', 'جمال', 1, 2548, 0, 1),
(2549, 'كيف-اعمل-غمازات', 'كيف اعمل غمازات', 'عمل الغمازات.', 'جمال', 1, 2549, 0, 1),
(2550, 'ما-معايير-الجمال-العالمية', 'ما معايير الجمال العالميه', 'مقاييس الجمال العالميه.', 'جمال', 1, 2550, 0, 1),
(2551, 'كيف-الحفاظ-على-الجمال-الخارجي-والداخلي', 'كيف الحفاظ على الجمال الخارجي والداخلي', 'كيف تحافظين على جمالك وجاذبيتك؟', 'جمال', 1, 2551, 0, 1),
(2552, 'ما-انواع-الوجوه', 'ما انواع الوجوه', 'ما هي اشكال الوجوه؟', 'جمال', 1, 2552, 0, 1),
(2553, 'كيف-تصبحين-ملكة-جمال', 'كيف تصبحين ملكه جمال', 'شروط ملكه الجمال.', 'جمال', 1, 2553, 0, 1),
(2554, 'ما-معايير-الجمال-عند-الرجال', 'ما معايير الجمال عند الرجال', 'بعض الصفات التي تفضلها النساء بالرجال.', 'جمال', 1, 2554, 0, 1),
(2555, 'مما-يتركب-الكحل-الأثمد', 'مما يتركب الكحل الاثمد', 'مكونات وفواءد الكحل الاثمد.', 'جمال', 1, 2555, 0, 1),
(2556, 'ما-أنواع-التقشير-الكيميائي', 'ما انواع التقشير الكيمياءي', 'انواع و فواءد التقشير الكيمياءي.', 'جمال', 1, 2556, 0, 1),
(2557, 'ما-طرق-رسم-الحواجب', 'ما طرق رسم الحواجب', 'كيف ارسم حواجبي؟', 'جمال', 1, 2557, 0, 1),
(2558, 'كم-الوقت-الذي-ينمو-به-شعر-الحواجب', 'كم الوقت الذي ينمو به شعر الحواجب', 'كم يستغرق نمو شعر الحواجب؟', 'جمال', 1, 2558, 0, 1),
(2559, 'ما-طريقه-ثقب-الأذن-في-البيت', 'ما طريقه ثقب الاذن في البيت', 'كيفيّه ثقب الاذن في المنزل؟', 'جمال', 1, 2559, 0, 1),
(2560, 'كيفية-تحضير-ماسك-لنفخ-الوجه', 'كيفيه تحضير ماسك لنفخ الوجه', 'اسرع ماسك لنفخ الوجه.﻿', 'جمال', 1, 2560, 0, 1),
(2561, 'كيفية-جعل-الساقين-خاليه-من-الشعر', 'كيفيه جعل الساقين خاليه من الشعر', 'كيفيه التخلص من شعر الساقين للنساء؟', 'جمال', 1, 2561, 0, 1),
(2562, 'لماذا-نفعل-ساونا', 'لماذا نفعل ساونا', 'ما فواءد الساونا؟', 'جمال', 1, 2562, 0, 1),
(2563, 'من-هم-أجمل-رجال-العالم', 'من هم اجمل رجال العالم', 'اسماء اجمل و اوسم رجال العالم.', 'جمال', 1, 2563, 0, 1),
(2564, 'ما-أنواع-الوشم', 'ما انواع الوشم', 'كم نوع للوشم؟', 'جمال', 1, 2564, 0, 1),
(2565, 'ما-هو-المايك', 'ما هو المايك', 'تعريف المايك.', 'منوعات تقنيه', 1, 2565, 0, 1),
(2566, 'كيفية-تقوية-الوايرلس', 'كيفيه تقويه الوايرلس', 'طرق لتقويه الوايرلس.', 'منوعات تقنيه', 1, 2566, 0, 1),
(2567, 'ما-أول-معركة-ه-زم-فيها-المسلمون', 'ما اول معركه هُزم فيها المسلمون', 'كانت معركه احد اول معركهٍ ينهزم فيها معسكر المسلمون في سلسله معاركهم ضد المشركين.', 'معارك و غزوات', 1, 2567, 0, 1),
(2568, 'ماذا-تعرف-عن-عمر-بن-الخطاب-قبل-الإسلام', 'ماذا تعرف عن عمر بن الخطاب قبل الاسلام', 'هو صحابي جليل، ومن اعظم الشخصيات في تاريخ الاسلام، واسمه عمر بن الخطاب بن نفيل بن عبد العزى بن رباح بن عبد الله بن قرط بن رزاح بن عدي.', 'حياه الرسول و الصحابه', 1, 2568, 0, 1),
(2569, 'ما-فوائد-الذرة-الصفراء', 'ما فواءد الذره الصفراء', 'يعتبر الذره من المحاصيل الزراعيه الهامّه جداً في العالم، حيث ينتمي الى مملكه النباتات، ورتبه الفبءيات، و الفصيله النجيليه.', 'منوعات في فن الطهي', 1, 2569, 0, 1),
(2570, 'كيفية-صنع-زيت-الحلبة', 'كيفيه صنع زيت الحلبه', 'يساعد زيت الحلبه في علاج مشكله الحبوب.', 'تنظيف و تقشير البشره', 1, 2570, 0, 1),
(2571, 'من-صاحب-أطول-أظافر-في-العالم', 'من صاحب اطول اظافر في العالم', 'جميعنا يعلم انّ الاظافر هي صفه جماليه يجب الاهتمام بها والحفاظ عليها.', 'العنايه باليدين', 1, 2571, 0, 1),
(2572, 'ما-هي-ألقاب-نادي-برشلونة', 'ما هي القاب نادي برشلونه', 'برشلونه، اسم نادٍ رياضيٍ اسبانيٍ شهيرٍ جداً، ومعروفٌ على مستوى العالم.', 'فنانين', 1, 2572, 0, 1),
(2573, 'ما-هي-السنن-المهجورة', 'ما هي السنن المهجوره', 'السنن المهجوره هي السنن القوليّه والفعليّه التي سنَّها النبي عليه الصلاه والسلام وشاع تركها بين المسلمين في وقتنا الحاضر.', 'الفروض و السنن', 1, 2573, 0, 1),
(2574, 'ما-مفهوم-صلاة-الغداة', 'ما مفهوم صلاه الغداه', 'معنى الغداه في اللغه ما بين الفجر وطلوع الشمس، والمقصود بصلاه الغداه صلاه الفجر.', 'معلومات اسلاميه', 1, 2574, 0, 1);
INSERT INTO `q_trigraph` (`id`, `id_url`, `title`, `body`, `keyword`, `type`, `id_ref`, `score`, `deleted`) VALUES
(2575, 'ما-أهمية-صلاة-الجماعة', 'ما اهميه صلاه الجماعه', 'تُعَدّ صلاه الجماعه في المسجد من افضل الطاعات التي يتقرّب بها العبد الى الله.', 'الفروض و السنن', 1, 2575, 0, 1),
(2576, 'كيفية-صلاة-الجمعة', 'كيفيه صلاه الجمعه', 'صلاه الجمعه عيد المسلمين كل اسبوعٍ؛ فيوم الجمعه احب الايام الى الله وهو خير يومٍ تطلع فيه الشمس.', 'فروض و سنن', 1, 2576, 0, 1),
(2577, 'ما-الحكمة-من-صلاة-الجمعة', 'ما الحكمه من صلاه الجمعه', 'شرع الله للناس اوقاتاً متعددهً للاجتماعات؛ منها: الاجتماع لاداء الصلوات الخمس كلّ يومٍ.', 'فروض و سنن', 1, 2577, 0, 1),
(2578, 'ما-شروط-عد-ة-الأرملة', 'ما شروط عدّه الارمله', 'في حال توفّى زوج المراه فعليها ان تبتعد عن التطيّب والزينه والتعرّض للخاطبين والخضاب اثناء فتره العدّه.', 'معلومات عامه', 1, 2578, 0, 1),
(2579, 'ما-هو-اليمين-الغموس', 'ما هو اليمين الغموس', 'حثّ الاسلام على حفظ الاَيمان وعدم نقضها، والّا يحلف بشيءٍ الّا ان يكون صادقاً.', 'الفروض و السنن', 1, 2579, 0, 1),
(2580, 'ما-معنى-اليمين-الغموس-في-الإسلام', 'ما معنى اليمين الغموس في الاسلام', 'حثّ الاسلام على حفظ الاَيمان وعدم نقضها، والّا يحلف بشيءٍ الّا ان يكون صادقاً، وقد رتَّبت الشريعه الاسلاميه على من اقسم امور واحكام تختلف بحسب اختلاف صيغه اليمين والمراد منه وكيفيه انشاءه.', 'معلومات اسلاميه', 1, 2580, 0, 1),
(2581, 'ما-علامات-ليلة-القدر', 'ما علامات ليله القدر', 'ليله القدر هي من اجل الليالي عند المسلمين كافهً في جميع بقاع الارض، وذلك لعظيم فضلها عند الله تعالى، فقد اخبر الله عباده عنها بانّها من احب الليالي اليه.', 'الفروض و السنن', 1, 2581, 0, 1),
(2582, 'ما-معنى-البر', 'ما معنى البر', 'لعلّ الوقوف على معنى البر ودلالات المصطلح في اللغه العربيه يكشف للباحثين علاقه المعنى اللغوي بالدلاله الاصطلاحيه له، على اعتبار انّ استخدامات العرب للمفردات اصلٌ في فهم معاني ودلالات الاصطلاحات.', 'مصطلحات و معاني', 1, 2582, 0, 1),
(2583, 'كيفية-بر-الوالدين', 'كيفيه بر الوالدين', 'لقد اولى الاسلام العظيم موضوع برّ الوالدين اهتماماً كبيراً واعتنى به عنايهً خاصه، فقد جعل الاسلام بر الوالدين اعظم البر وافضل الاعمال بعد الصلاه المكتوبه.', 'مواضيع دينيه متفرقه', 1, 2583, 0, 1),
(2584, 'ما-حق-الزوجة-على-زوجها', 'ما حق الزوجه على زوجها', 'شرعت الديانات السماويه الثلاث الزواج ونظمته، اذ حددت له شروطاً وحدوداً لضمان حقوق الازواج، وذلك لتكون رابطاً قوياً ومتيناً ومعيناً على الاستمرار والديمومه.', 'معلومات اسلاميه', 1, 2584, 0, 1),
(2585, 'ما-شروط-صحة-الزواج', 'ما شروط صحه الزواج', 'انّ الاسره اساس المجتمع، والبناء القاءم عليه، والاساس الذي تقوم عليه الاسره الزوجين، حيث قال الله تعالى: (يَا اَيُّهَا النَّاسُ اِنَّا خَلَقْنَاكُم مِّن ذَكَرٍ وَاُنثَى وَجَعَلْنَاكُمْ شُعُوبًا وَقَبَاءِلَ لِتَعَارَفُوا).', 'معاملات اسلاميه', 1, 2585, 0, 1),
(2586, 'ما-هي-أهداف-الزواج', 'ما هي اهداف الزواج', 'خلقَ الله سُبحانهُ وتعالى زوجينِ اثنين من كُلّ شيءٍ فكانت هذهِ الثُناءيات في الكون من السُنَنَ الثابته.', 'مواضيع دينيه متفرقه', 1, 2586, 0, 1),
(2587, 'ما-أبرز-أعمال-مارلين-مونرو', 'ما ابرز اعمال مارلين مونرو', 'هي فنانه امريكيّه الجنسيّه، واسمها الاصلي هو نورما جين موتينسن، ولكنها عدلته فيما بعد ليصبح نورما جين بيكر.', 'فنانين', 1, 2587, 0, 1),
(2588, 'من-هو-قصي-خولي', 'من هو قصي خولي', 'هو ممثّل سوري من مواليد 29 ابريل عام 1976.', 'فنانين', 1, 2588, 0, 1),
(2589, 'من-هو-دافنشي', 'من هو دافنشي', 'يُعَدّ ليوناردو دا فينشي احدَ اهمِّ، واشهرِ الرسّامين في التاريخِ، وقد كانَ معروفاً بانّه ذو شخصيّهٍ مُفكِّرهٍ، ومُمارِسهٍ للفنّ.', 'فنانين', 1, 2589, 0, 1),
(2590, 'ما-أهم-أعمال-دافنشي', 'ما اهم اعمال دافنشي', 'بدات موهبهُ دا فينشي تظهرُ عندما بلغ عُمره خمسهَ عشرَ عاماً؛ فارسله والده ليتعلَّمَ من النحّاتِ، والرسّام الشهيرِ اندريا ديل فيروتشيو.', 'فنانين', 1, 2590, 0, 1),
(2591, 'من-هي-أجمل-ممثلة-سورية', 'من هي اجمل ممثله سوريه', 'ولدت الممثله الحسناء نسرين طافش في 15 فبراير من عام 1982م.', 'فنانين', 1, 2591, 0, 1),
(2592, 'من-هي-أجمل-ممثلة-تركية', 'من هي اجمل ممثله تركيه', 'في السنوات الاخيره غزت الدراما التركيه المدبلجه القنوات التلفزيونيه العربيه ، ودخلت بيوتنا وتلفزيوناتنا ، وباتت الدراما والمسلسلات التركيه هي اهتمام العرب من النساء والاطفال والرجال ايضاً.', 'فنانين', 1, 2592, 0, 1),
(2593, 'ما-أهم-أعمال-بيتهوفن-الموسيقية', 'ما اهم اعمال بيتهوفن الموسيقيه', '&nbsp;السر وراء استعمال بيتهوفن للهورن خلال الحركه الثالثه، والاوبوا خلال الحركه الرابعه في معزوفته.', 'فنانين', 1, 2593, 0, 1),
(2594, 'من-الذي-غنى-عيرتني-بالشيب', 'من الذي غنى عيرتني بالشيب', 'يعتبر ناظم الغزالي من المطربين العراقيين الاشهر في خمسينات وستينات القرن الماضي ولد ناظم الغزالي في بغداد يتم الاب وكانت امه ضريره فكانت حالتهم فقيره جدا.', 'فنانين', 1, 2594, 0, 1),
(2595, 'من-هي-ليلى-علوي', 'من هي ليلى علوي', 'الفنانه المصريه ليلى علوي من مواليد 4 يناير 1962، و هي احد اهم ممثلات السينما المصريه الحديثه ، متزوجه من السيد منصور الجمّال ولهما ابن اسمه خالد .', 'فنانين', 1, 2595, 0, 1),
(2596, 'من-هي-الفنانة-سهير-رمزي', 'من هي الفنانه سهير رمزي', 'الفنانه سهير رمزي ممثله مصريه من مواليد الثالث من مارس عام 1950 في بورسعيد ، اسمها الحقيقي سهير محمد عبد السلام نوح.', 'فنانين', 1, 2596, 0, 1),
(2597, 'من-هو-أبو-ليلى-الخطيب', 'من هو ابو ليلى الخطيب', 'ابو ليلى الخطيب، هي شخصيه جسدها الفنان السوري ايمن رضا، وهي من الاعمال الجديده التي قدمها وحازت على شهره واسعه جدا، بسبب طريقه التمثيل المحبه للمشاهد، من قبل هذا الفنان الراءع.', 'فنانين', 1, 2597, 0, 1),
(2598, 'من-هي-الفنانة-سميرة-توفيق', 'من هي الفنانه سميره توفيق', 'المغنيه اللبنانيه سميره توفيق من مواليد 25 ديسمبر 1935 في قريه ام حارتين في محافظه السويداء ، اسمها الحقيقي سميره كريمونه.', 'فنانين', 1, 2598, 0, 1),
(2599, 'ما-هي-حادثة-الإفك', 'ما هي حادثه الافك', 'خرج النبيّ -عليه السلام- في احدى غزواته، وكانت معه السيده عاءشه وذلك في السنه السادسه، او الخامسه للهجره، وكان عمر السيده عاءشه حين ذاك نحو خمس عشره سنهٍ.', 'صحابيات', 1, 2599, 0, 1),
